import React, { useEffect, useState } from 'react';
import SkillIcon from './SkillIcon';
import '../styles/app.css';
import colors from '../data/colors';

const Projects = ({ projects }) => {
	return (
		<div className="component-container">
			<div className="component-header">Projects</div>
			<div className="grid-2">
				{projects.map((project, i) => {
					return (
						<ProjectCardView
							project={project}
							index={i}
							key={i}
						/>
					);
				})}
			</div>
		</div>
	);
};

const ProjectCardView = ({ project, index }) => {
	const [underline, setUnderline] = useState(false);
	let techstack =
		window.innerWidth > 1330
			? project.techstack
			: project.mobileTechstack;

	useEffect(() => {
		if (window.innerWidth > 1330) {
			techstack = project.techstack;
		} else {
			techstack = project.mobileTechstack;
		}
	}, [window]);

	const openLink = () => {
		if (project.link) {
			window.open(project.link);
		}
	};

	const handleMouseEnter = () => {
		if (project.link) {
			setUnderline(true);
		}
	};

	return (
		<div className="card">
			<div
				className={`card-title${underline ? ' underline' : ''}`}
				onClick={openLink}
				onMouseEnter={handleMouseEnter}
				onMouseLeave={() => setUnderline(false)}
			>
				{project.title}
			</div>
			<div
				className={`card-desc${underline ? ' underline' : ''}`}
				onClick={openLink}
				onMouseEnter={handleMouseEnter}
				onMouseLeave={() => setUnderline(false)}
			>
				{' '}
				{project.description}
			</div>
			<div className="card-tags HStack">
				{techstack &&
					techstack.map((tech, i) => {
						return (
							<SkillIcon
								skill={tech}
								color={colors[tech] || 'red'}
								key={i}
							/>
						);
					})}
			</div>
		</div>
	);
};

export default Projects;
