import React, { useState } from 'react';
import '../../styles/experience.css';

const Experiences = ({ experiences }) => {
	const [selection, setSelection] = useState(0);
	return (
		<div className="experience-container">
			<div className="experience-header">Experience</div>
			<ExperienceSelect
				experiences={experiences}
				selection={selection}
				onSelect={(s) => setSelection(s)}
			/>
			<ExperienceDisplay
				experiences={experiences}
				selection={selection}
			/>
		</div>
	);
};

const ExperienceDisplay = ({ experiences, selection }) => {
	return (
		<div className="experience-display-container">
			<ul>
				{experiences[selection].descriptions.map((desc, i) => {
					return <li key={i}>{desc}</li>;
				})}
			</ul>
		</div>
	);
};

const ExperienceSelect = (props) => {
	const { experiences, selection } = props;

	return (
		<div className="experience-selector">
			<div className="selector-container">
				{experiences.map(({ company }, i) => {
					return (
						<div
							className={`selection${
								selection === i ? ' selected' : ''
							}`}
							onClick={() => props.onSelect(i)}
							key={i}
						>
							{company}
						</div>
					);
				})}
			</div>
		</div>
	);
};

export default Experiences;
